rule "Calculate CPU Percentage"
when
    true
then
    let current_message = to_string($message.result);
    let cpu_total_usage = regex("cpu_stats.*?cpu_usage.*?total_usage=(\\d+)", current_message)["0"];
    let system_cpu_usage = regex("cpu_stats.*?cpu_usage.*?system_cpu_usage=(\\d+)", current_message)["0"];
    let num_cpus = regex("cpu_stats.*?online_cpus=(\\d+)", current_message)["0"];

    let precpu_total_usage = regex("precpu_stats.*?cpu_usage.*?total_usage=(\\d+)", current_message)["0"];
    let system_precpu_usage = regex("precpu_stats.*?cpu_usage.*?system_cpu_usage=(\\d+)", current_message)["0"];

    let cpu_delta = to_double(cpu_total_usage) - to_double(precpu_total_usage);
    let system_cpu_delta = to_double(system_cpu_usage) - to_double(system_precpu_usage);

    let cpu_usage_percentage = (to_double(cpu_delta) / to_double(system_cpu_delta)) * to_double(num_cpus) * 100.0;

    set_field("cpu_usage_percentage", to_double(cpu_usage_percentage));
end

rule "Calculate Memory Percentage"
when
    true
then
    let current_message = to_string($message.result);
    let memory_usage = regex("memory_stats.*?usage=(\\d+)", current_message)["0"];
    let memory_limit = regex("memory_stats.*?limit=(\\d+)", current_message)["0"];

    let memory_usage_percentage = (to_double(memory_usage) / to_double(memory_limit)) * 100.0;

    set_field("memory_usage_percentage", to_double(memory_usage_percentage));
end
